import React from "react";
import "./signupdiv.css";
import InputLabel from '@material-ui/core/InputLabel';
// import NativeSelect from '@material-ui/core/NativeSelect';
import Select from '@material-ui/core/Select';
import { MenuItem } from "@material-ui/core";
// import { Redirect } from 'react0-router-dom';
// import history from './history';
import axios from "axios";
import swal from 'sweetalert';

// const { pool, Client } = require('pg');

class SignupDiv extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            role: "",
            password: "",
            cpassword: "",
        };
    }
    ValidateEmail(mail) {
        if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(mail)) {
            return (true)
        }
        swal("You have entered an invalid email address!", "error")
        return (false)
    }
    signup = (e) => {
        e.preventDefault();

        if (this.state.name === "") {
            swal("Enter your name", "error");
        } else if (this.state.email === "") {
            alert("Enter your Email ID");
        } else if (this.state.password !== this.state.cpassword) {
            alert("passwords does not match");
        } else {
            if (this.state.role === "teacher") {
                let formData = new FormData();
                formData.set("name", this.state.name);
                const test = this.ValidateEmail(this.state.email);
                if (test === false) {
                    alert('Enter a valid Email ID')
                }
                else {
                    formData.set("email", this.state.email);
                }
                formData.set("password", this.state.password);
                axios.post("http://localhost:5000/signupteacher", formData).then(res => {
                    if (res.data.success === true) {
                        console.log('data entered successfully')
                    } else {
                        console.log(res);
                    }
                });

            }
            if (this.state.role === "student") {
                let formData = new FormData();
                formData.set("name", this.state.name);
                const test = this.ValidateEmail(this.state.email);
                if (test === false) {
                    alert('Enter a valid Email ID')
                }
                else {
                    formData.set("email", this.state.email);
                }

                formData.set("password", this.state.password);
                axios.post("http://localhost:5233/signupstudent", formData).then(res => {
                    if (res.data.success === true) {
                        const users = res.data.token;
                        this.setState({ ...this.state, name: "bk" });
                        localStorage.setItem("user", users);
                        console.log("user registered");
                        this.props.history.push({
                            pathname: "/",
                            state: { isteacher: true }
                        });
                    } else {
                        console.log(res);
                    }
                });

            }
        }

    }
    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };
    handlechange = (event) => {
        console.log(event.target.value)
        this.setState({ role: (event.target.value) })
    }
    render() {
        return (
            <div>

                <div className="signupset" align="center">
                    <form className="signup">
                        <input
                            className="form-alignment"
                            type="text"
                            id="name"
                            name="name"
                            placeholder="Name"
                            className="form-alignments"
                            onChange={e => this.handleChange(e)}
                        />
                        <input
                            type="email"
                            id="email"
                            name="email"
                            placeholder="Email"
                            className="form-alignments"
                            onChange={e => this.handleChange(e)}
                        />
                        {/* <div className="col-25">
                        <label htmlFor="fname">Create an account as ?</label>
                    </div>
                    <div className="col-75">
                        <select id="role" className="selectstyle" name="role">
                            <option value="Teacher">Teacher</option>
                            <option value="Student">Student</option>

                        </select>
                    </div> */}
                        {/* <div align="center" className="selectset"> */}
                        <InputLabel id="demo-customized-select-label">Create an account as </InputLabel>
                        <Select
                            id="demo-customized-select-native"
                            value={this.state.role}
                            onChange={this.handlechange}>
                            <MenuItem value="">
                                <em>None</em>
                            </MenuItem>
                            <MenuItem value={"teacher"}>Teacher</MenuItem>
                            <MenuItem value={"student"}>Student</MenuItem>
                        </Select>
                        {/* </div> */}
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Password"
                            className="form-alignments"
                            onChange={e => this.handleChange(e)}
                        />
                        <input
                            type="password"
                            id="confirm"
                            name="cpassword"
                            placeholder="Confirm Password"
                            className="form-alignments"
                            onChange={e => this.handleChange(e)}
                        />
                        <button
                            className="buttonstyle"
                            id="send"
                            onClick={e => {
                                this.signup(e);
                            }}>
                            Signup
            </button>
                        <div className="buttins">
                            <p className="para">Have an account?</p>
                            <button className="buttonstyle">
                                <a className="line" href="/">
                                    Login</a>
                            </button>
                        </div>
                    </form>
                </div>
            </div>)
    }
}
export default SignupDiv;
