import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import React, { useEffect } from 'react';
import clsx from 'clsx';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AssignmentTurnedInIcon from '@material-ui/icons/AssignmentTurnedIn';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import ListItemText from '@material-ui/core/ListItemText';
import './Adminhome.css';
import Axios from 'axios';

const drawerWidth = 240
const useStyles = makeStyles((theme) => ({

    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    hide: {
        display: 'none',
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
}));


export default function AdminHome() {
    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = React.useState(false);
    const [Accessarray, setAccessarray] = React.useState([]);
    const [Assignarray, setAssignarray] = React.useState([]);
    const [Subjectarray, setSubjectarray] = React.useState([]);
    const [Accessview, setAccessview] = React.useState(false);
    const [Assignview, setAssignview] = React.useState(false);
    const accessset = () => {
        setAccessview(true);
    }
    const assignset = () => {
        setAssignview(true);
    }
    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };
    useEffect(() => {
        Axios.get(`http://localhost:5000/getaccesslist`)
            .then(res => {
                console.log(res.data.data);
                setAccessarray(res.data.data);
                // console.log(Commentarray);
            })
        Axios.get(`http://localhost:5000/getassignlist`)
            .then(res => {
                console.log(res.data.data);
                setAssignarray(res.data.data);
                // console.log(Answerarray);
            })
        Axios.get(`http://localhost:5000/getsubject`)
            .then(res => {
                console.log(res.data.data);
                setSubjectarray(res.data.data);

            })

    }, [])
    return (
        <div>
            <div className='drawersetup'>
                <CssBaseline />
                <AppBar
                    position="fixed"
                    className={clsx(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            className={clsx(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Testing Engine
          </Typography>
                    </Toolbar>
                </AppBar>
                <Drawer className={classes.drawer}
                    variant="persistent"
                    anchor="left"
                    open={open}
                    classes={{
                        paper: classes.drawerPaper,
                    }}>
                    <div className={classes.drawerHeader}>
                        <IconButton onClick={handleDrawerClose}>
                            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                        </IconButton>
                    </div>
                    <Divider />
                    <List>
                        <ListItem button onClick={accessset()} key={'Student Access'}>
                            <ListItemIcon>< AccountCircleIcon /></ListItemIcon>
                            <ListItemText primary={'Student Access'} />
                        </ListItem>
                        <ListItem button onClick={assignset()} key={'Subject Assignment'}>
                            <ListItemIcon><AssignmentTurnedInIcon /></ListItemIcon>
                            <ListItemText primary={'Subject Assignment'} />
                        </ListItem>

                    </List>
                </Drawer>

            </div>
            <div>

            </div>

        </div >
    )
}
