// import React from 'react';
import { BrowserRouter as withRouter } from 'react-router-dom'
import Test from '../../components/createtest/test';
import { makeStyles, useTheme, withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import React, { useEffect } from 'react';
import history from '../../history';
// import { BrowserRouter as Router, Route, Switch, withRouter } from "react-router-dom";
import clsx from 'clsx';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AssignmentTurnedInIcon from '@material-ui/icons/AssignmentTurnedIn';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import ListItemText from '@material-ui/core/ListItemText';
import './homepage.css';
import Axios from 'axios';
import swal from 'sweetalert';

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({

    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    hide: {
        display: 'none',
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
}));

function Homepage(props) {
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const classes = useStyles();
    const [Tests, setTests] = React.useState(false);
    const handleDrawerOpen = () => {
        setOpen(true);
    };
    const viewresult = () => {

    }
    const logout = () => {
        localStorage.removeItem('teacher')
        props.history.push('/')

    }
    const handleDrawerClose = () => {
        setOpen(false);
    };
    const createtest = () => {
        setTests(true);
    }

    return (
        <div>
            <div className='drawersetup'>
                <CssBaseline />
                <AppBar
                    position="fixed"
                    className={clsx(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}>
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            className={clsx(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Testing Engine
          </Typography>
                    </Toolbar>
                </AppBar>
                <Drawer className={classes.drawer}
                    variant="persistent"
                    anchor="left"
                    open={open}
                    classes={{
                        paper: classes.drawerPaper,
                    }}>
                    <div className={classes.drawerHeader}>
                        <IconButton onClick={handleDrawerClose}>
                            < AccountCircleIcon />  Teacher
                            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                        </IconButton>
                    </div>
                    <Divider />
                    <div className="listset">
                        <button className="buttonlist" onClick={e => createtest()}><span>Create Test</span></button>
                    </div>
                    <div className="listset">
                        <button className="buttonlist" onClick={e => viewresult()}><span>View Results</span></button>
                    </div>
                    <div className="listset">
                        <button className="buttonlist" onClick={e => logout()}>Logout</button>
                    </div>
                </Drawer>
                <Test></Test>
                <div>{true ? <Test></Test> : <></>}</div>
            </div>
        </div>);
}

export default (Homepage);
